    <script>

        $('.isDate:not([readonly])').datepicker({
            language: "pt-BR",
            autoclose: true,
            todayHighlight: true,
            format: 'dd/mm/yyyy',
            orientation: "bottom auto"
        });

        $('.isDateRange').daterangepicker({
            autoUpdateInput: false,
            "locale": {
                "format": "DD/MM/YYYY",
                "separator": " - ",
                "applyLabel": "Aplicar",
                "cancelLabel": "Cancelar",
                "daysOfWeek": [
                "Dom",
                "Seg",
                "Ter",
                "Qua",
                "Qui",
                "Sex",
                "Sab"
                ],
                "monthNames": [
                "Janeiro",
                "Fevereiro",
                "Março",
                "Abril",
                "Maio",
                "Junho",
                "Julho",
                "Agosto",
                "Setembro",
                "Outubro",
                "Novembro",
                "Dezembro"
                ],
                "firstDay": 1
            }
        }, function(start_date, end_date) {
            this.element.val(start_date.format('DD/MM/YYYY')+' - '+end_date.format('DD/MM/YYYY'));
        });


        function deleteRegister(registro) {
            const url = registro.getAttribute("data-rota");
            swal.fire({
                title: 'Deseja excluir esse registro?',
                text: "O registro atualmente selecionado será excluído.",
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Sim',
                cancelButtonText: 'Não'
            }).then(function (success) {
                if (success.isConfirmed) {
                    window.location.href = url;
                }
            })
        }

    </script>
